sparql:
select distinct ?uri ?createur ?createurLabel ?image ?titre_fr where { 
                                values ?qid { wd:Q50488927 }
                                values ?classRel { wdt:P31 }
                                values ?class { wd:Q16743958 wd:Q16743958 }
                                values ?rel { wdt:P136 }
                                ?uri ?rel ?qid; ?classRel  wd:Q3305213 ;
                                    wdt:P170 ?createur ;
                                    wdt:P18 ?image .
                                ?qid ?classRel ?class .
                                ?createur rdfs:label ?createurLabel .
                                filter(lang(?createurLabel)="fr")
                                ?qid ?classRel ?class .
                                OPTIONAL { ?uri wdt:P1476 ?titre_fr. }  
                                FILTER((LANG(?titre_fr)) = "fr")
                    }
